// Main JavaScript for all pages in Arabic
document.addEventListener('DOMContentLoaded', function() {
    // Back to top button
    const backToTopButton = document.getElementById('back-to-top');
    
    if (backToTopButton) {
        window.addEventListener('scroll', function() {
            if (window.pageYOffset > 300) {
                backToTopButton.style.display = 'block';
            } else {
                backToTopButton.style.display = 'none';
            }
        });
        
        backToTopButton.addEventListener('click', function() {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        });
    }
    
    // Smooth scrolling for anchor links
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            if (this.getAttribute('href') === '#') return;
            
            e.preventDefault();
            const targetId = this.getAttribute('href');
            const targetElement = document.querySelector(targetId);
            
            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
                
                // Update URL without jumping
                if (history.pushState) {
                    history.pushState(null, null, targetId);
                } else {
                    location.hash = targetId;
                }
            }
        });
    });
    
    // Contact form handling
    const contactForm = document.getElementById('contact-form');
    if (contactForm) {
        contactForm.addEventListener('submit', function(e) {
            e.preventDefault();
            const formData = new FormData(this);
            
            // Here you would typically send the form data to a server
            fetch('submit-form.php', {
                method: 'POST',
                body: formData
            })
            .then(response => {
                if (response.ok) {
                    alert('شكرًا لك على رسالتك! سنتواصل معك قريبًا.');
                    contactForm.reset();
                } else {
                    throw new Error('Network response was not ok');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('حدثت مشكلة في إرسال رسالتك. يرجى المحاولة مرة أخرى لاحقًا.');
            });
        });
    }
    
    // Initialize tooltips
    const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
    tooltipTriggerList.map(function(tooltipTriggerEl) {
        return new bootstrap.Tooltip(tooltipTriggerEl);
    });
    
    // Add page class to body based on current page
    const path = window.location.pathname;
    const page = path.split("/").pop();
    
    // Remove all page classes first
    document.body.classList.remove(
        'home-page', 
        'about-page', 
        'services-page', 
        'tests-page', 
        'contact-page'
    );
    
    // Add appropriate class based on current page
    switch(page) {
        case 'index.html':
        case '':
            document.body.classList.add('home-page');
            break;
        case 'about.html':
            document.body.classList.add('about-page');
            break;
        case 'services.html':
            document.body.classList.add('services-page');
            break;
        case 'tests.html':
            document.body.classList.add('tests-page');
            break;
        case 'contact.html':
            document.body.classList.add('contact-page');
            break;
        default:
            document.body.classList.add('home-page');
    }
    
    // Handle disease selection from URL hash
    if (window.location.hash && document.getElementById('disease-tests')) {
        const diseaseHash = window.location.hash.substring(1);
        if (diseases && diseases[diseaseHash]) {
            const diseaseCard = document.querySelector(`.start-assessment[data-disease="${diseaseHash}"]`);
            if (diseaseCard) {
                diseaseCard.click();
            }
        }
    }
});
